INSERT INTO `truyen` (`id`, `tentruyen`, `tacgia`, `tomtat`, `danhmuc_id`, `hinhanh`, `slug_truyen`, `kichhoat`, `created_at`, `updated_at`, `truyen_noibat`) VALUES
(8, 'KIẾM VỰC VÔ ĐỊCH', 'thanh loan phong thượng', 'Dương Diệp, một đệ tử tạp dịch của Kiếm Tông ở Nam Vực, trong một lần vô tình có được vòng xoáy thần bí, từ đó bước lên con đường kiếm đạo khác với mọi người.\r\n\r\nCảnh giới trong truyện được chia làm Phàm Nhân cảnh, Tiên Thiên cảnh, Vương Giả cảnh, Giả cảnh, Tôn Giả cảnh…\r\n\r\nCông pháp vũ khí chia làm Thiên Địa Huyền Hoàng, Thiên cấp cao nhất, Hoàng cấp thấp nhất.\r\n\r\nTông môn chia từ nhất phẩm đến cửu phẩm, nhất phẩm lót đáy, cửu phẩm thống ngự cửu vực.\r\n\r\nVăn phong của truyện sâu sắc, khá hài hước, sẽ có rất nhiều danh ngôn để độc giả cười đau cả bụng và nhớ đời chẳng hạn:\r\n\r\n- Ta bằng bản lĩnh của mình mượn được tiền, vì sao phải trả?\r\n\r\n- Con người một khi lên được cao rồi, thường thường sẽ quên mình từ phía dưới đi lên.', 5, 'kiem-vuc-vo-dich75.jpg', 'kiem-vuc-vo-dich', 0, '2023-04-24 00:50:01', '2023-04-24 02:25:44', 1),
(9, 'CÁI KỊCH BẢN SÁT NHÂN NÀY TUYỆT ĐỐI CÓ VẤN ĐỀ', 'Quý Xuân Thập Nguyệt', 'Bạn thích Thám tử Conan với những vụ án sát nhân trong phòng kín, bạn thích chơi trò ma sói thật thật giả giả, bạn thích chơi những game kinh dị giải đố với những tình tiết xoắn não…\r\n\r\nHãy đến với Kịch bản sát nhân, người chơi bị ép nhập vai vào những nhân vật trong một vụ án giết người, với mục tiêu chung là tìm cho ra bằng được hung thủ.', 1, 'cai-kich-ban-sat-nhan-nay-tuyet-doi-co-van-de51.jpg', 'cai-kich-ban-sat-nhan-nay-tuyet-doi-co-van-de', 0, '2023-04-24 02:00:22', '2023-04-24 02:02:36', 0),
(10, 'KẾT MINH HÔN CÔ VỢ TRẺ LÀ MA', 'Dạ Vô Thanh', 'Người từ lúc được sinh ra cho đến khi chết đi chính là một quá trình, một khi đã chết cũng liền trút một hơi.\r\n\r\nNhưng trên đời này luôn có người không muốn tắt thở, thi thể không muốn tiến vào quan tài, quỷ không muốn đi phủ. Người làm nghề đạo sĩ như chúng tôi, nói trắng ra chính là đi cắt đứt hơi thở cuối cùng mà người chết không muốn nuốt xuống.\r\n\r\nBất quá, ai véo cổ ai, thì phải xem người đó có bản lĩnh hay không.\r\n\r\nTrong rừng núi hoang vu, khu mồ mã yên tĩnh, rừng cây u ám.\r\n\r\nChung quanh luôn có một số cấm địa không muốn bị người sống đặt chân ……', 1, 'ket-minh-hon-co-vo-tre-la-ma73.jpg', 'ket-minh-hon-co-vo-tre-la-ma', 0, '2023-04-24 02:01:13', '2023-04-24 02:25:30', 2),
(11, 'NỮ TRINH THÁM VÀ SÁT THỦ', 'Mạc Nhan', 'Là duyên hay là nợ thì tình yêu vẫn luôn đẹp theo cách riêng của nó, mỗi chúng ta sẽ có được tình cảm của riêng bản thân mình.\r\n\r\nCô là một trinh sát giỏi bị sát thủ theo dõi và đang có mưu đồ sát hại cô. Nhưng sau khi hắn đối diện với cô, nhìn sâu vào đôi mắt ấy chân tay dường như không còn chút sức lực nữa, hắn không thể hạ sát cô.\r\n\r\nNgược lại hắn còn mong muốn cô làm nữ chủ nhân của mình để có thể chăm sóc bảo vệ cho cô. Chẳng biết cô và hắn là do duyên hay nợ, chỉ biết rằng những lúc ở bên nhau giống như con thiêu thân đang lao vào lửa.', 2, 'nu-trinh-tham-va-sat-thu21.jpg', 'nu-trinh-tham-va-sat-thu', 0, '2023-04-24 02:03:23', NULL, 0),
(12, 'TRINH THÁM ĐẠI THIẾU GIA', 'Eagle Phạm', 'Logan (A Long) là con của một đại gia đình giàu có và đầy quyền lực tại đất Sài Gòn. Tuy giàu có, nhưng bên trong gia đình anh là cả một tổ chức đen tối có thể thao túng được rất nhiều thế lực bao gồm cả cảnh sát hoặc chính phủ. \r\n\r\nCác thành viên trong gia đình luôn đấu tranh với nhau một cách mãnh liệt, dùng mọi thủ đoạn nhằm nắm quyền cai trị toàn bộ thành phố, mở rộng lãnh địa.\r\n\r\nTrong môi trường sống như vậy, Logan lại là trẻ mồ côi, không người chăm sóc. Nhưng trời cao đã phú cho anh một cái đầu nhạy bén, minh mẫn và một cặp mắt có thể nhìn thấu tâm lý của người khác thông qua những chi tiết dù chỉ là nhỏ nhất. Anh là một thiên tài trong bộ môn Tâm lý tội phạm học. Bên cạnh anh còn Anna, người bạn thân từ thửa nhỏ, một cô gái nhìn bề ngoài luôn lạnh lùng như băng tuyết nhưng lại có một trái tim nhân hậu, và đặc biệt cô rất giỏi võ công. \r\n\r\nCùng Anna, Logan sẽ vướng vào những âm mưu đẫm máu của chính gia đình anh, từ đó anh sẽ khám phá ra nhiều bí mật đen tối bên trong và tự gầy dựng lại tương lai của chính mình mà không cần phải dựa vào bất cứ gì từ gia đình anh.\r\n\r\nCâu chuyện sẽ diễn biến như thế nào? Những vụ án sảy ra nhằm vào điều gì? Logan phải nắm bắt tương lai của chính mình ra sao? Mời bạn đọc Bộ Tiểu Thuyết: trinh Thám Đại Thiếu Gia', 2, 'trinh-tham-dai-thieu-gia86.jpg', 'trinh-thám-dại-thiéu-gia', 0, '2023-04-24 02:04:54', NULL, 0),
(13, 'ĐẾN TRỐN TRONG VÒNG TAY CỦA TÔI', 'Tuế Kiến', 'Sau khi chia thành nghệ thuật và khoa học, bạn cùng bàn của Lâm Tiêu là Giang Yến, một lão đại nổi tiếng tại Trường trung học số 10.\r\n\r\nMọi người trong Trường trung học số 10 đều biết rằng Giang Yến lão đại là một người cáu kỉnh và kiêu ngạo và đối xử với các nữ sinh như thể một tai họa.\r\n\r\nLâm Tiêu hiểu rõ chuyện này và tránh xa anh.\r\n\r\nAi biết rằng không lâu sau, vẫn có người ngấm ngầm tung tin đồn rằng Giang Yến thích cô.\r\n\r\nĐể chứng minh sự “trong sạch” của mình, Lâm Tiêu đã điều tra kĩ tin đồn, nhưng đáng tiếc là cô đã quay trở lại mà không thành công.\r\n\r\nBuộc phải làm như vậy, cô không còn cách nào ngoài việc mắng người này trước mặt Giang Yến: “Thật quá đáng! Nếu tôi bắt được người này! Tôi nhất định sẽ ----” Cô chưa kịp nói xong, Giang Yến đã đột nhiên cắt ngang lời cô: “Nhất định làm sao?”\r\n\r\nLâm Tiêu tràn đầy phẫn nộ: “Tôi nhất định giết hắn!!!”\r\n\r\nGiang Yến sau khi nghe vây, đột nhiên cúi đầu, nghịch ngợm cười đến gần cô: “Giết tôi liệu có quá tàn nhẫn không?”\r\n\r\n“.....!”\r\n\r\n-\r\n\r\nNhiều năm sau.\r\n\r\nMột câu hỏi nào đó: Trong tình yêu, bạn có bí quyết gì mà đối phương không biết?\r\n\r\nTrong số hàng chục ngàn câu trả lời, hai người dùng ẩn danh nổi bật.\r\n\r\nNgười dùng ẩn danh: Anh ấy có thể không biết rằng bài đăng về việc anh ấy thích tôi thực sự là do tôi đăng.\r\n\r\nNgười dùng ẩn danh: Có lẽ cô ấy chưa biết, tôi thực sự biết rằng cô ấy đã đăng bài viết về việc tôi thích cô ấy trên Tieba trước đó.\r\n\r\nNgay khi hai câu trả lời được đăng, đã nhanh chóng vượt mốc một triệu lượt thích, thu hút một lượng lớn cư dân mạng để lại lời nhắn yêu cầu theo dõi.\r\n\r\nVài ngày sau, người dùng ẩn danh đã trả lời bài đăng thứ hai đã cập nhật nó.\r\n\r\nGiang Yến: theo dõi, được biết đến, đã kết hôn với cô ấy\r\n\r\n“Nếu thế gian quá cay đắng, chi bằng đến trốn dưới vòng tay của anh ”', 4, 'den-tron-trong-vong-tay-cua-toi21.jpg', 'den-tron-trong-vong-tay-cua-toi', 0, '2023-04-24 02:06:55', NULL, 0),
(14, 'TỔNG TÀI ANH NHẬN NHẦM NGƯỜI RỒI', 'Nguyệt Hạ', 'Bạn đang đọc truyện Tổng Tài, Anh Nhận Nhầm Người Rồi của tác giả Nguyệt Hạ. Cô là nhị tiểu thư thời gia, bề ngoài là một đứa ngốc đầu óc có vấn đề, thực tế lại là người thao túng Kim Tiền Đế Quốc, nhà tư Kim Bài.\r\n\r\nHắn là thiếu gia Thịnh gia, 5 năm trước vì chuyện tiền bạc bị người khác tính kế, suýt chút mất mạng trong biển lửa, từ đó giả vờ bị mù để báo thù.\r\n\r\nHai người vì liên hôn giữa hai gia đình mà gặp nhau, cô bất đắc dĩ trở thành vợ anh, từ đó anh trở thành \"vật cản đường\" thành công của cô, cô cũng trở thành kẻ ngáng đường tinh thần của anh.\r\n\r\n\"Tôi không phải vợ chưa cưới của anh, anh nhận nhầm người rồi!\"\r\n\r\n\"Không, cô chính là cô ấy!\"', 6, 'tong-tai-anh-nhan-nham-nguoi-roi-98768475.jpg', 'tong-tai-anh-nhan-nham-nguoi-roi', 0, '2023-04-24 02:25:00', '2023-04-24 02:25:16', 1),
(15, 'BẤT CHẤP YÊU ANH', 'Nàng tiên rảnh rang tới mức nhàm chán', 'Ba mươi tuổi vẫn chưa kết hôn, bà mai giới thiệu cho tôi một kẻ s.át nhân.\r\n\r\nSau khi bị tôi phát hiện, ánh mắt bà mai né tránh: \"Hiện giờ cậu ấy đã thay đổi rồi... Không còn g.iết người nữa.\"\r\n\r\nĐến khi bạn tôi biết chuyện thì luôn miệng nói việc này quá đ.iên rồ, nhưng tôi đã chủ động thêm bạn với anh sau khi xem ảnh của anh.', 6, 'bat-chap-yeu-anh71.jpg', 'bat-chap-yeu-anh', 0, '2023-04-24 02:27:51', NULL, 0),
(16, 'TÌNH YÊU CỦA ANH TÔI KHÔNG DÁM NHẬN', 'Thi Thi', 'Bạn đang đọc truyện Tình Yêu Của Anh Tôi Không Dám Nhận của tác giả Thi Thi. Kiều Phương Hạ cảm thấy mình ở đâu cũng là một người thừa.\r\n\r\nLúc bé thì ba mẹ ly hôn dù cho cô lóc van xin....\r\n\r\nMẹ cô lại thành vợ bé của người khác, hai mẹ con cùng tới Lệ gia, nhưng dĩ nhiên họ không chào đón\r\n\r\nMẹ cô đẩy cô ra, nói cô phải làm thân với con trai tình nhân của mẹ.\r\n\r\nSau này cô lớn lên, cô tự hiểu mình chỉ là một cô gái xa lạ ăn nhờ ở đậu nơi này.\r\n\r\nThế nhưng tối qua, mọi việc xảy ra hoàn toàn không phải do cô.\r\n\r\nLệ Đình Tuấn trong đêm tối cúp điện kia bỗng nhiên lôi kéo cô vào phòng, còn giày vò cô cả đêm, cô chỉ là một cô gái mềm ếu không thể phản kháng được.\r\n\r\nTới hôm sau mệt mỏi tỉnh lại, cô trở thành kẻ đê tiện trong mắt hết thảy mọi người', 6, 'tinh-yeu-cua-anh-toi-khong-dam-nhan-49969719.jpg', 'tinh-yeu-cua-anh-toi-khong-dam-nhan', 0, '2023-04-24 02:29:44', NULL, 1);

